import React, { useState } from "react";
import { ethers } from "ethers";
import { JsonRpcProvider } from "@ethersproject/providers";
import { connectWallet, signTransaction } from "@mysten/wallet-adapter"; // Sui SDK function (simplified)

const App = () => {
  const [ethereumAccount, setEthereumAccount] = useState("");
  const [suiAccount, setSuiAccount] = useState("");
  const [amount, setAmount] = useState("");

  // Connect to Ethereum Wallet (MetaMask)
  const connectEthereumWallet = async () => {
    if (typeof window.ethereum !== "undefined") {
      try {
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        setEthereumAccount(accounts[0]);
      } catch (error) {
        console.error("MetaMask connection failed: ", error);
      }
    } else {
      alert("MetaMask is not installed!");
    }
  };

  // Connect to Sui Wallet
  const connectSuiWallet = async () => {
    try {
      const suiWallet = await connectWallet();
      setSuiAccount(suiWallet.address);
    } catch (error) {
      console.error("Sui Wallet connection failed: ", error);
    }
  };

  // Bridge tokens from Ethereum to Sui
  const bridgeToSui = async () => {
    if (!ethereumAccount || !suiAccount || !amount) {
      alert("Please connect both wallets and specify an amount.");
      return;
    }

    try {
      const provider = new ethers.providers.JsonRpcProvider("http://127.0.0.1:8545"); // Local Ethereum provider
      const signer = provider.getSigner(ethereumAccount);

      // Call burn function on Ethereum contract
      const ethContractAddress = "0xYourEthereumContractAddress"; // Replace with your Ethereum contract address
      const ethAbi = [
        "function burn(uint256 amount) public"
      ];
      const ethContract = new ethers.Contract(ethContractAddress, ethAbi, signer);
      await ethContract.burn(ethers.utils.parseUnits(amount, 18));

      // Call mint function on Sui contract
      const suiTransaction = {
        packageObjectId: "0xYourSuiPackageId", // Replace with your Sui package ID
        module: "suipart",
        function: "mint",
        arguments: [
          suiAccount,
          parseInt(amount, 10),
        ],
      };
      await signTransaction(suiTransaction); // Sui SDK function

      alert("Tokens bridged successfully!");
    } catch (error) {
      console.error("Error bridging tokens: ", error);
      alert("Failed to bridge tokens.");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <h1 className="text-2xl font-bold mb-4">Blockchain Bridge ðŸŒ‰</h1>

      <div className="mb-4">
        <button 
          onClick={connectEthereumWallet} 
          className="px-4 py-2 bg-blue-500 text-white rounded shadow">
          Connect Ethereum Wallet
        </button>
        {ethereumAccount && <p>Connected Ethereum: {ethereumAccount}</p>}
      </div>

      <div className="mb-4">
        <button 
          onClick={connectSuiWallet} 
          className="px-4 py-2 bg-green-500 text-white rounded shadow">
          Connect Sui Wallet
        </button>
        {suiAccount && <p>Connected Sui: {suiAccount}</p>}
      </div>

      <div className="mb-4">
        <input
          type="number"
          placeholder="Amount to Bridge"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="p-2 border rounded w-64"
        />
      </div>

      <button
        onClick={bridgeToSui}
        className="px-4 py-2 bg-purple-500 text-white rounded shadow">
        Bridge Tokens to Sui
      </button>
    </div>
  );
};

export default App;
