import React, { useState } from "react";
import { ethers } from "ethers";
import { parseUnits } from "ethers";
import { useWallet, ConnectButton } from "@suiet/wallet-kit";

const App = () => {
  const [ethereumAccount, setEthereumAccount] = useState("");
  const [amount, setAmount] = useState("");
  const wallet = useWallet(); 

  const connectEthereumWallet = async () => {
    if (typeof window.ethereum !== "undefined") {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        setEthereumAccount(accounts[0]);
      } catch (error) {
        console.error("MetaMask connection failed: ", error);
      }
    } else {
      alert("MetaMask is not installed!");
    }
  };

  const convertETHToIBT = async () => {
    if (!ethereumAccount || !amount) {
      alert("Please connect your Ethereum wallet and specify an amount.");
      return;
    }

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const ethContractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";
      const ethAbi = ["function convertETHToIBT() public payable"];
      const ethContract = new ethers.Contract(ethContractAddress, ethAbi, signer);

      await ethContract.convertETHToIBT({
        value: ethers.utils.parseEther(amount), 
      });

      alert(`Successfully converted ${amount} ETH to IBT!`);
    } catch (error) {
      console.error("Error converting ETH to IBT: ", error);
      alert("Failed to convert ETH to IBT.");
    }
  };

  const bridgeToSui = async () => {
    if (!ethereumAccount || !wallet.connected || !amount) {
      alert("Please connect both wallets and specify an amount.");
      return;
    }

    try {
      const provider = new ethers.providers.JsonRpcProvider(
        "http://127.0.0.1:8545"
      );
      const signer = provider.getSigner(ethereumAccount);
      const ethContractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3"; 
      const ethAbi = ["function burn(address from, uint256 amount) public"];
      const ethContract = new ethers.Contract(ethContractAddress, ethAbi, signer);

      await ethContract.burn(ethereumAccount, parseUnits(amount, 18)); 

      const suiTransaction = {
        packageObjectId: "0x9330eb39cba20119511b782e48f2695ab242414e80342b0a69972d0bf4e32298", 
        module: "suipart",
        function: "mint",
        arguments: [wallet.address, parseFloat(amount)], 
      };

      await wallet.signAndExecuteTransaction({
        transaction: {
          kind: "moveCall",
          data: suiTransaction,
        },
      });

      alert(`Successfully bridged ${amount} IBT to Sui!`);
    } catch (error) {
      console.error("Error bridging tokens: ", error);
      alert("Failed to bridge tokens.");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <h1 className="text-2xl font-bold mb-4">Blockchain Bridge 🌉</h1>

      <div className="mb-4">
        <button
          onClick={connectEthereumWallet}
          className="px-4 py-2 bg-blue-500 text-white rounded shadow"
        >
          Connect Ethereum Wallet
        </button>
        {ethereumAccount && <p>Connected Ethereum: {ethereumAccount}</p>}
      </div>

      <div className="mb-4">
        <ConnectButton />
        {wallet.connected && <p>Connected Sui: {wallet.address}</p>}
      </div>

      <div className="mb-4">
        <input
          type="number"
          placeholder="Amount (ETH)"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="p-2 border rounded w-64"
        />
      </div>

      <div className="flex space-x-4">
        <button
          onClick={convertETHToIBT}
          className="px-4 py-2 bg-green-500 text-white rounded shadow"
        >
          Convert ETH to IBT
        </button>
        <button
          onClick={bridgeToSui}
          className="px-4 py-2 bg-purple-500 text-white rounded shadow"
        >
          Bridge IBT to Sui
        </button>
      </div>
    </div>
  );
};

export default App;
